padBuildHtml:
{$intro}

{item}
  {tag}  A {first}   
  {middle} B {/middle} 
  {last}   C {/last}     
{/item}

<hr>
{item}
  {first}  {example app='reference', page='{$dir}/{$item}', skipClose}           {tag:first}   
  {middle} {example app='reference', page='{$dir}/{$item}', skipOpen, skipClose} {/middle} 
  {last}   {example app='reference', page='{$dir}/{$item}', skipOpen}            {/last}     
{/item}

padContent:
{if ! #skipOpen}
  <table border="1" cellpadding="10" cellspacing="0">
{/if}

  <tr>
    <th bgcolor="#dddddd">PHP <br><font size="-2">{$padExampleFilePhp} </font></th>
    <th bgcolor="#dddddd">HTML<br><font size="-2">{$padExampleFileHtml}</font></th>
    {if ! #skipResult}
      <th bgcolor="#dddddd">Result</th>
    {/if}
  </tr>

  <tr>
    <td style="vertical-align:top">{!padExampleSrcPhp} </td>
    <td style="vertical-align:top">{!padExampleSrcHtml}</td>
    {if ! #skipResult}
      <td style="vertical-align:top">{ajax app='{$padExampleApp}', page='{$padExamplePage}'}</td> 
    {/if}
  </tr>

{if ! #skipClose}
  </table>
{/if}

